import { Component } from '@angular/core';
import { FormControl, FormGroup } from '@angular/forms';
import { MatFormFieldModule } from '@angular/material/form-field';
import { MatSelectModule } from '@angular/material/select';
import { race } from 'rxjs';
import { RestApiService } from 'src/services/rest-api.service';


@Component({
  selector: 'app-drop-down-form',
  templateUrl: './drop-down-form.component.html',
  styleUrls: ['./drop-down-form.component.sass']
})
export class DropDownFormComponent {

form!: FormGroup;
categories: { name: string, id: string }[] = [];
reqParam = {};

constructor(private rest: RestApiService) {}
ngOnInit() {
  this.form = new FormGroup({
    category: new FormControl()
  });

  this.rest.getRemove(null,'character/dropStart', this.reqParam,'get').subscribe(

    (data: any) => {
      let races: any[];
      let classes: any[];
      data.forEach((element: any) => {
        if (races && races.includes(element.id)) {
          classes.push(element);
        } else {
          races.push( element );
        }
        this.categories.concat(...races, ...classes);

      });
      console.log(this.categories);
    }
   );
}


onSubmit(formData: any) {
  console.log(formData);
  // form submission logic goes here
}

}
<form [formGroup]="form">
  <mat-form-field>
    <mat-label>Category</mat-label>
    <mat-select formControlName="category">
      <mat-option *ngFor="let category of categories" [value]="category.id">
        {{category.name}}
      </mat-option>
    </mat-select>
  </mat-form-field>
</form>
